<script>
gsap.registerPlugin(ScrollTrigger);
export default {
    mounted() {
        let imac = gsap.timeline({
            scrollTrigger: {
                trigger: ".svg1",
                start: "top center",
                end: "bottom center",
                scrub: 1,
            }
        })

        let imacText = gsap.timeline({
            scrollTrigger: {
                trigger: ".svg1",
                start: "top center",
                end: "bottom-=400 center",
                scrub: 1,
            }
        })

        let imacMoblie = gsap.timeline({
            scrollTrigger: {
                trigger: "#imac-text",
                start: "top center",
                end: "bottom center",
                scrub: 1,
            }
        })

        let imacTextMobile = gsap.timeline({
            scrollTrigger: {
                trigger: "#imac-text",
                start: "top center",
                end: "bottom-=400 center",
                scrub: 1,
            }
        })

        if (window.innerWidth < 640) {
            imacMoblie.from(".svg1", { x: 100, opacity: 0, duration: 1 })
                .from("#right-charts", { y: -50, opacity: 0, duration: 1 }, "-=.5")
                .from("#keyboard", { y: -5, x: 40, opacity: 0, duration: 1 }, "-=.5")
                .from("#left-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")
                .from("#right-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")
                .from("#mid-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")

            imacTextMobile.from("#imac-text", { x: -100, opacity: 0, duration: 1 }) 
        } else {
            imac.from(".svg1", { x: 100, opacity: 0, duration: 1 })
                .from("#right-charts", { y: -50, opacity: 0, duration: 1 }, "-=.5")
                .from("#keyboard", { y: -5, x: 40, opacity: 0, duration: 1 }, "-=.5")
                .from("#left-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")
                .from("#right-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")
                .from("#mid-antenna", { y: 35, opacity: 0, duration: 1 }, "-=.5")

            imacText.from("#imac-text", { x: -100, opacity: 0, duration: 1 })
        }

    }
}
</script>

